public partial class List : System.Web.DynamicData.PageTemplate\n   {\n       protected void Page_Load(object sender, EventArgs e)\n       {\n           var table = this.Table;\n           var tablePermissionAttr = table.GetAttribute<TablePermissionAttribute>();\n\n           if (tablePermissionAttr != null)\n           {\n               var permissionProperty = tablePermissionAttr.PermissionProperty;\n               var user = (CustomUser)HttpContext.Current.User;\n\n               var canViewProperty = user.GetType().GetProperty(permissionProperty);\n               if (canViewProperty != null)\n               {\n                   var canView = (bool)canViewProperty.GetValue(user);\n\n                   if (!canView)\n                   {\n                       Response.Redirect("~/AccessDenied.aspx");\n                   }\n               }\n               else\n               {\n                   Response.Redirect("~/AccessDenied.aspx");\n               }\n           }\n       }\n   }