using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Windows;\nusing System.Windows.Resources;\n\npublic static List<string> GetImageList()\n{\n    List<string> imageList = new List<string>();\n    // Get the current assembly\n    var assembly = System.Reflection.Assembly.GetExecutingAssembly();\n    // Get the resource name of the assembly\n    string assemblyName = assembly.GetName().Name;\n\n    // Define the path to the resources\n    string resourcePath = $"/{assemblyName};component/";\n\n    // Get the resource dictionary\n    var resourceDictionary = Application.LoadComponent(new Uri(resourcePath + "Resources.xaml", UriKind.Relative)) as ResourceDictionary;\n    \n    if (resourceDictionary != null)\n    {\n        foreach (var key in resourceDictionary.Keys)\n        {\n            if (resourceDictionary[key] is Uri)\n            {\n                string resourceUri = resourceDictionary[key].ToString();\n                if (resourceUri.EndsWith(".png") || resourceUri.EndsWith(".jpg") || resourceUri.EndsWith(".jpeg") || resourceUri.EndsWith(".bmp"))\n                {\n                    imageList.Add(resourceUri);\n                }\n            }\n        }\n    }\n\n    return imageList;\n}