using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string dllName = "mydll.dll"; // Replace with the name of your DLL\n        Process process = Process.GetCurrentProcess(); // Get the current process\n\n        foreach (ProcessModule module in process.Modules)\n        {\n            if (module.ModuleName.Equals(dllName, StringComparison.OrdinalIgnoreCase))\n            {\n                IntPtr baseAddress = module.BaseAddress;\n                Console.WriteLine($"Base address of {dllName}: {baseAddress}");\n                break;\n            }\n        }\n    }\n}