using System.ServiceModel;\nusing System.ServiceModel.Web;\n\n[ServiceContract]\npublic interface IMetaWeblog\n{\n    [OperationContract]\n    [WebInvoke(Method = "POST", UriTemplate = "metaweblog.newPost", RequestFormat = WebMessageFormat.Xml, ResponseFormat = WebMessageFormat.Xml)]\n    string NewPost(string blogid, string username, string password, Post post, bool publish);\n\n    [OperationContract]\n    [WebInvoke(Method = "POST", UriTemplate = "metaweblog.getRecentPosts", RequestFormat = WebMessageFormat.Xml, ResponseFormat = WebMessageFormat.Xml)]\n    List<Post> GetRecentPosts(string blogid, string username, string password, int numberOfPosts);\n\n    // Other MetaWeblog methods...\n}\n\n[DataContract]\npublic class Post\n{\n    [DataMember]\n    public string Title { get; set; }\n\n    [DataMember]\n    public string Description { get; set; }\n\n    // Other post properties...\n}