// Assuming 'myCanvas' is your Canvas and 'myUserControl' is your UserControl\nCanvas.SetZIndex(myUserControl, 1); // Brings 'myUserControl' to the front with a higher ZIndex value\n\n// To ensure another control is behind, you can set a lower ZIndex value\nvar anotherUserControl = new UserControl();\nCanvas.SetZIndex(anotherUserControl, 0); // This will render 'anotherUserControl' behind 'myUserControl'\n\n// Add the controls to the canvas\nmyCanvas.Children.Add(anotherUserControl);\nmyCanvas.Children.Add(myUserControl);