using System;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Using X509Certificate\n        X509Certificate cert = new X509Certificate("path_to_certificate.cer");\n        Console.WriteLine("Issuer: " + cert.Issuer);\n        Console.WriteLine("Subject: " + cert.Subject);\n\n        // Using X509Certificate2\n        X509Certificate2 cert2 = new X509Certificate2("path_to_certificate.cer");\n        Console.WriteLine("Issuer: " + cert2.Issuer);\n        Console.WriteLine("Subject: " + cert2.Subject);\n        Console.WriteLine("Thumbprint: " + cert2.Thumbprint);\n        Console.WriteLine("Has Private Key: " + cert2.HasPrivateKey);\n    }\n}