using System;\nusing System.Web.UI;\n\npublic partial class Countries : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            ApplyFilter();\n        }\n    }\n\n    protected void ButtonFilter_Click(object sender, EventArgs e)\n    {\n        string filterQuery = this.CheckBoxNAME.Checked ? $"NAME LIKE '%{this.TextBoxNAME.Text}%'" : "";\n        SqlDataSourceCOUNTRIES.FilterExpression = filterQuery;\n    }\n\n    private void ApplyFilter()\n    {\n        string name = Request.QueryString["NAME"];\n        if (!string.IsNullOrEmpty(name))\n        {\n            this.TextBoxNAME.Text = name;\n            this.CheckBoxNAME.Checked = true;\n\n            string filterQuery = $"NAME LIKE '%{name}%'";\n            SqlDataSourceCOUNTRIES.FilterExpression = filterQuery;\n        }\n    }\n}