private void FindLinksInDiv()\n{\n    HtmlDocument document = webBrowser1.Document;\n    if (document != null)\n    {\n        HtmlElementCollection divs = document.GetElementsByTagName("div");\n        foreach (HtmlElement div in divs)\n        {\n            if (div.GetAttribute("className") == "XYZ")\n            {\n                HtmlElementCollection abcElements = div.GetElementsByTagName("ABC");\n                foreach (HtmlElement abcElement in abcElements)\n                {\n                    HtmlElementCollection links = abcElement.GetElementsByTagName("a");\n                    foreach (HtmlElement link in links)\n                    {\n                        string href = link.GetAttribute("href");\n                        // Process the href or other information as needed\n                        Console.WriteLine(href);\n                    }\n                }\n            }\n        }\n    }\n}