using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define your connection string (update with your actual database connection details)\n        string connectionString = "Server=your_server;Database=your_database;User Id=your_username;Password=your_password;";\n\n        // Define your SQL insert query\n        string insertQuery = "INSERT INTO YourTableName (Column1, Column2) VALUES (@Value1, @Value2)";\n\n        // Create the SqlConnection and SqlCommand\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(insertQuery, connection);\n            \n            // Add parameters to avoid SQL injection\n            command.Parameters.AddWithValue("@Value1", "SomeValue1");\n            command.Parameters.AddWithValue("@Value2", "SomeValue2");\n\n            try\n            {\n                // Open the connection\n                connection.Open();\n                \n                // Execute the insert command\n                int rowsAffected = command.ExecuteNonQuery();\n                \n                // Optionally, check the number of rows affected\n                Console.WriteLine($"{rowsAffected} row(s) inserted");\n            }\n            catch (Exception ex)\n            {\n                // Handle any errors that may have occurred\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}