// App.xaml.cs\npublic partial class App : PrismApplication\n{\n    protected override void RegisterTypes(IContainerRegistry containerRegistry)\n    {\n        // Register services and repositories\n        containerRegistry.Register<IProductRepository, ProductRepository>();\n        containerRegistry.Register<IPOSService, POSService>();\n    }\n\n    protected override void ConfigureModuleCatalog(IModuleCatalog moduleCatalog)\n    {\n        // Register modules\n        moduleCatalog.AddModule<MainModule>();\n    }\n\n    protected override Window CreateShell()\n    {\n        return Container.Resolve<MainWindow>();\n    }\n}\n\n// ProductRepository.cs\npublic class ProductRepository : IProductRepository\n{\n    private readonly MyDbContext _context;\n    public ProductRepository(MyDbContext context)\n    {\n        _context = context;\n    }\n\n    public async Task<IEnumerable<Product>> GetAllProductsAsync()\n    {\n        return await _context.Products.ToListAsync();\n    }\n}\n\n// POSService.cs\npublic class POSService : IPOSService\n{\n    private readonly IProductRepository _productRepository;\n    public POSService(IProductRepository productRepository)\n    {\n        _productRepository = productRepository;\n    }\n\n    public async Task ProcessSaleAsync(Sale sale)\n    {\n        // Business logic for processing sale\n    }\n}