using System.Web.Mvc;\nusing Microsoft.Security.Application; // AntiXSS library\n\npublic class HomeController : Controller\n{\n    [HttpPost]\n    public ActionResult SaveContent(string content)\n    {\n        // Sanitize the input to prevent XSS\n        string sanitizedContent = Sanitizer.GetSafeHtmlFragment(content);\n\n        // Save sanitizedContent to the database\n        // Example: SaveToDatabase(sanitizedContent);\n\n        return RedirectToAction("Index");\n    }\n}