using Oracle.ManagedDataAccess.Client;\nusing System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=myUsername;Password=myPassword;Data Source=myOracleDB";\n\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n\n            using (OracleCommand command = new OracleCommand("SELECT * FROM MyTable", connection))\n            {\n                using (OracleDataReader reader = command.ExecuteReader())\n                {\n                    DataTable dataTable = new DataTable();\n                    dataTable.Load(reader);\n\n                    // Display data in a DataGrid or other UI components\n                }\n            }\n        }\n    }\n}