public void WriteToDatabases(string query)\n{\n    string primaryConnectionString = ConfigurationManager.ConnectionStrings["PrimaryDatabase"].ConnectionString;\n    string secondaryConnectionString = "Data Source=SecondaryServer;Initial Catalog=YourDatabase;User ID=YourUsername;Password=YourPassword;";\n\n    using (SqlConnection primaryConnection = new SqlConnection(primaryConnectionString))\n    {\n        using (SqlCommand command = new SqlCommand(query, primaryConnection))\n        {\n            primaryConnection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n\n    using (SqlConnection secondaryConnection = new SqlConnection(secondaryConnectionString))\n    {\n        using (SqlCommand command = new SqlCommand(query, secondaryConnection))\n        {\n            secondaryConnection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n}