// Assuming param is a string in the format 'parametername:value'\nstring[] paramParts = param.Split(':');\nif (paramParts.Length == 2)\n{\n    string paramName = paramParts[0];\n    string paramValue = paramParts[1];\n\n    // repDoc is the current ReportDocument\n    ParameterFieldDefinitions parameterFieldDefinitions = repDoc.DataDefinition.ParameterFields;\n    ParameterFieldDefinition parameterFieldDefinition = parameterFieldDefinitions[paramName];\n\n    ParameterValues currentParameterValues = new ParameterValues();\n    ParameterDiscreteValue discreteValue = new ParameterDiscreteValue\n    {\n        Value = paramValue\n    };\n\n    currentParameterValues.Add(discreteValue);\n    parameterFieldDefinition.ApplyCurrentValues(currentParameterValues);\n\n    // Export the report to PDF\n    repDoc.ExportToHttpResponse(ExportFormatType.PortableDocFormat, response, false, "Report");\n}\nelse\n{\n    // Handle the error where the parameter format is incorrect\n    throw new ArgumentException("Parameter format is incorrect. Expected format is 'parametername:value'.");\n}