public bool WaitForPendingFinalizersWithTimeout(int millisecondsTimeout)\n   {\n       var finalizerThread = new Thread(GC.WaitForPendingFinalizers);\n       finalizerThread.Start();\n       bool completed = finalizerThread.Join(millisecondsTimeout);\n       if (!completed)\n       {\n           // Handle the timeout scenario, e.g., log an error\n       }\n       return completed;\n   }