using System;\nusing System.IO;\nusing ZstdNet;\n\npublic class ZstdCompression\n{\n    private readonly byte[] dictionary;\n\n    public ZstdCompression(byte[] dictionary)\n    {\n        this.dictionary = dictionary;\n    }\n\n    public byte[] Compress(byte[] data)\n    {\n        using (var compressor = new Compressor(new ZstdNet.CompressionOptions(dictionary)))\n        {\n            return compressor.Wrap(data);\n        }\n    }\n\n    public byte[] Decompress(byte[] compressedData)\n    {\n        using (var decompressor = new Decompressor(new ZstdNet.DecompressionOptions(dictionary)))\n        {\n            return decompressor.Unwrap(compressedData);\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        byte[] exampleDictionary = File.ReadAllBytes("path_to_pretrained_dictionary");\n        ZstdCompression zstd = new ZstdCompression(exampleDictionary);\n        \n        byte[] data = File.ReadAllBytes("path_to_data_file");\n        byte[] compressedData = zstd.Compress(data);\n\n        byte[] decompressedData = zstd.Decompress(compressedData);\n        Console.WriteLine("Compression and Decompression successful: " + (BitConverter.ToString(data) == BitConverter.ToString(decompressedData)));\n    }\n}