// Example usage in a Console Application\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var config = ConfigLoader.LoadConfiguration("path/to/sharedConfig.json");\n        var setting1 = config["Setting1"].ToString();\n        Console.WriteLine($"Setting1: {setting1}");\n    }\n}\n\n// Example usage in a Web Application\npublic class HomeController : Controller\n{\n    public IActionResult Index()\n    {\n        var config = ConfigLoader.LoadConfiguration("path/to/sharedConfig.json");\n        var setting1 = config["Setting1"].ToString();\n        ViewBag.Setting1 = setting1;\n        return View();\n    }\n}