using System;\n   using System.Web.UI;\n   using System.Web.UI.WebControls;\n   using AjaxControlToolkit;\n   using Microsoft.SharePoint;\n   using Microsoft.SharePoint.WebPartPages;\n\n   namespace CustomSharePointWebPart\n   {\n       public class HtmlEditorWebPart : WebPart\n       {\n           protected override void CreateChildControls()\n           {\n               base.CreateChildControls();\n\n               ScriptManager scriptManager = ScriptManager.GetCurrent(Page) ?? new ScriptManager();\n               if (ScriptManager.GetCurrent(Page) == null)\n               {\n                   Controls.Add(scriptManager);\n               }\n\n               HtmlEditorExtender htmlEditor = new HtmlEditorExtender();\n               TextBox textBox = new TextBox { TextMode = TextBoxMode.MultiLine, Rows = 10, Columns = 40 };\n               htmlEditor.TargetControlID = textBox.ID;\n\n               Controls.Add(textBox);\n               Controls.Add(htmlEditor);\n\n               Button saveButton = new Button { Text = "Save" };\n               saveButton.Click += SaveButton_Click;\n               Controls.Add(saveButton);\n           }\n\n           private void SaveButton_Click(object sender, EventArgs e)\n           {\n               TextBox textBox = (TextBox)FindControl("textBox");\n               if (textBox != null)\n               {\n                   string htmlContent = textBox.Text;\n\n                   // Save the HTML content to a SharePoint list or field\n                   SPWeb web = SPContext.Current.Web;\n                   SPList list = web.Lists["YourListName"];\n                   SPListItem item = list.Items.Add();\n                   item["YourFieldName"] = htmlContent;\n                   item.Update();\n               }\n           }\n       }\n   }