using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputFile = "path/to/your/inputfile.txt";\n        string outputFile = "path/to/your/outputfile.gpg";\n        string recipient = "recipient@example.com";\n\n        EncryptFile(inputFile, outputFile, recipient);\n    }\n\n    static void EncryptFile(string inputFile, string outputFile, string recipient)\n    {\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = "gpg",\n            Arguments = $"--encrypt --recipient {recipient} --output {outputFile} {inputFile}",\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        using (Process process = new Process { StartInfo = startInfo })\n        {\n            process.Start();\n            string result = process.StandardOutput.ReadToEnd();\n            process.WaitForExit();\n\n            Console.WriteLine(result);\n        }\n    }\n}