using Oracle.ManagedDataAccess.Client;\nusing System;\nusing System.Data;\n\npublic DataTable GetPagedRecords(int pageNumber, int pageSize)\n{\n    string connectionString = "your_connection_string";\n    DataTable dataTable = new DataTable();\n\n    using (OracleConnection connection = new OracleConnection(connectionString))\n    {\n        using (OracleCommand command = new OracleCommand("GetPagedRecords", connection))\n        {\n            command.CommandType = CommandType.StoredProcedure;\n\n            command.Parameters.Add("p_page_number", OracleDbType.Int32).Value = pageNumber;\n            command.Parameters.Add("p_page_size", OracleDbType.Int32).Value = pageSize;\n            command.Parameters.Add("p_cursor", OracleDbType.RefCursor).Direction = ParameterDirection.Output;\n\n            connection.Open();\n            using (OracleDataReader reader = command.ExecuteReader())\n            {\n                dataTable.Load(reader);\n            }\n        }\n    }\n\n    return dataTable;\n}