protected void Grid_ItemDataBound(object sender, GridItemDataBoundEventArgs e)\n{\n    if (e.Item is GridDataItem)\n    {\n        GridDataItem dataItem = (GridDataItem)e.Item;\n        DateTime specificDate = new DateTime(2023, 1, 1); // Replace with the specific date you want to check\n\n        DateTime cellValue;\n        bool isDateTime = DateTime.TryParse(dataItem["YourDateTimeColumn"].Text, out cellValue);\n\n        if (isDateTime && cellValue == specificDate)\n        {\n            dataItem["YourDateTimeColumn"].Text = string.Empty;\n        }\n    }\n}