using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class YourClass\n{\n    public int? ForeignKeyField { get; set; }\n\n    public void UpdateRecord(int id, int? foreignKeyField)\n    {\n        using (SqlConnection conn = new SqlConnection("YourConnectionString"))\n        {\n            conn.Open();\n\n            using (SqlCommand cmd = new SqlCommand("UPDATE YourTable SET ForeignKeyField = @ForeignKeyField WHERE Id = @Id", conn))\n            {\n                cmd.Parameters.AddWithValue("@Id", id);\n                cmd.Parameters.AddWithValue("@ForeignKeyField", (object)foreignKeyField ?? DBNull.Value);\n\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}\n\n// Usage\npublic class Program\n{\n    public static void Main()\n    {\n        YourClass instance = new YourClass();\n        instance.UpdateRecord(1, null); // This will set the ForeignKeyField to NULL in the database\n    }\n}