var groupQuery = from table in MyTable.AsEnumerable()\n                 group table by new\n                 {\n                     Col1 = table["Column1"],\n                     Col2 = table["Column2"]\n                 } into groupedTable\n                 select new\n                 {\n                     x = groupedTable.Key.Col1,\n                     y = groupedTable.Key.Col2,\n                     Count = groupedTable.Count()\n                 };