using System;\nusing System.Data;\nusing System.Data.OracleClient; // Ensure you have the correct Oracle client library\n\npublic DataTable GetTableSchema(string tableName, OracleConnection connection)\n{\n    string query = @"\n        SELECT COLUMN_NAME, DATA_TYPE, DATA_LENGTH, DATA_PRECISION, DATA_SCALE, NULLABLE\n        FROM USER_TAB_COLUMNS\n        WHERE TABLE_NAME = :tableName";\n\n    using (OracleCommand cmd = new OracleCommand(query, connection))\n    {\n        cmd.Parameters.Add("tableName", OracleType.VarChar).Value = tableName.ToUpper();\n\n        using (OracleDataAdapter adapter = new OracleDataAdapter(cmd))\n        {\n            DataTable schemaTable = new DataTable();\n            adapter.Fill(schemaTable);\n            return schemaTable;\n        }\n    }\n}\n\n// Usage\n// Assuming you have a valid Oracle connection in `conn`\nOracleConnection conn = new OracleConnection("your connection string here");\nconn.Open();\nDataTable schema = GetTableSchema("YOUR_TABLE_NAME", conn);\nconn.Close();\n\n// Display schema information (for example)\nforeach (DataRow row in schema.Rows)\n{\n    Console.WriteLine($"Column: {row["COLUMN_NAME"]}, Type: {row["DATA_TYPE"]}, Length: {row["DATA_LENGTH"]}");\n}