public class ConfigManager\n{\n    private static readonly string configUrl = "http://configserver/config/CachePolicy.xml";\n    private static Timer configPollTimer;\n    \n    public static void Start()\n    {\n        LoadConfig();\n        configPollTimer = new Timer(PollConfig, null, TimeSpan.Zero, TimeSpan.FromMinutes(5));\n    }\n\n    private static void LoadConfig()\n    {\n        try\n        {\n            var configFile = new WebClient().DownloadString(configUrl);\n            // Process the configuration file\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions\n        }\n    }\n\n    private static void PollConfig(object state)\n    {\n        LoadConfig();\n    }\n}\n\n// Call ConfigManager.Start() during application startup