using System;\nusing System.Web.Services;\nusing System.Web.Services.Protocols;\n\n[WebService(Namespace = "http://example.com/")]\n[WebServiceBinding(ConformsTo = WsiProfiles.BasicProfile1_1)]\npublic class VerificationService : WebService\n{\n    public class AuthHeader : SoapHeader\n    {\n        public string Username;\n        public string Password;\n    }\n\n    public AuthHeader Authentication;\n\n    [WebMethod]\n    [SoapHeader("Authentication", Required = true)]\n    public string Verify(string AccountNumber, string ProductName)\n    {\n        if (Authentication == null || !IsUserAuthenticated(Authentication.Username, Authentication.Password))\n        {\n            throw new SoapException("Unauthorized", SoapException.ClientFaultCode);\n        }\n\n        // Your verification logic here\n        return "Verified";\n    }\n\n    private bool IsUserAuthenticated(string username, string password)\n    {\n        // Implement your authentication logic here\n        return username == "yourUsername" && password == "yourPassword";\n    }\n}