using System.Drawing;\n\n// Assume 'graphics' is your Graphics object\nGraphics graphics = ...;\n\nfloat dpiX = graphics.DpiX;\nfloat dpiY = graphics.DpiY;\n\n// Function to convert from world units to points\nfloat WorldToPoint(float worldUnit)\n{\n    // 1 point is 1/72 inch\n    float pointsPerInch = 72.0f;\n    return worldUnit * (pointsPerInch / dpiX);\n}\n\n// Example usage\nfloat worldUnit = 10.0f; // This is your measurement in world units\nfloat pointUnit = WorldToPoint(worldUnit);\n\nConsole.WriteLine($"World Unit: {worldUnit}, Point Unit: {pointUnit}");