protected void ScriptManager1_AsyncPostBackError(object sender, AsyncPostBackErrorEventArgs e)\n{\n    // Log the error to trigger health monitoring\n    LogError(e.Exception);\n\n    // Re-throw the exception to ensure the error is shown to the user\n    ScriptManager1.AsyncPostBackErrorMessage = e.Exception.Message;\n}\n\nprivate void LogError(Exception ex)\n{\n    // Using health monitoring to log the error\n    System.Web.Management.WebBaseEvent.Raise(new System.Web.Management.WebRequestErrorEvent("AsyncPostBackError", this, 100001, ex));\n}