using WebSupergoo.ABCpdf9;\n\npublic void CreatePDF()\n{\n    Doc theDoc = new Doc();\n    theDoc.Rect.Inset(20, 20); // Set margin\n\n    // Create a text style with no paragraph indent\n    TextStyle ts = theDoc.GetTextStyle();\n    ts.FirstLineIndent = 0;\n\n    theDoc.SetTextStyle(ts);\n\n    // Add text to the first column\n    theDoc.Rect.String = "50 700 250 750"; // Define the first column\n    theDoc.AddText("This is a paragraph that will be chained to the next column. ");\n\n    // Add text to the second column\n    theDoc.Rect.String = "300 700 500 750"; // Define the second column\n    theDoc.AddText("Continuing the paragraph without indent.");\n\n    // Save the document\n    theDoc.Save("output.pdf");\n    theDoc.Clear();\n}