using System;\n    using Opc.Ua;\n    using Opc.Ua.Client;\n    using Opc.Ua.Configuration;\n\n    namespace OpcUaClient\n    {\n        class Program\n        {\n            static void Main(string[] args)\n            {\n                // Create the application configuration.\n                ApplicationInstance application = new ApplicationInstance\n                {\n                    ApplicationName = "OPC UA Client",\n                    ApplicationType = ApplicationType.Client,\n                    ConfigSectionName = "OpcUaClient"\n                };\n\n                application.LoadApplicationConfiguration(false).Wait();\n                application.CheckApplicationInstanceCertificate(false, 0).Wait();\n\n                // Create a session with the OPC UA server.\n                var endpointURL = "opc.tcp://localhost:4840";\n                var endpoint = CoreClientUtils.SelectEndpoint(endpointURL, useSecurity: false);\n                var session = Session.Create(application.ApplicationConfiguration, new ConfiguredEndpoint(null, endpoint, EndpointConfiguration.Create(application.ApplicationConfiguration)), false, "", 60000, null, null).Result;\n\n                Console.WriteLine("Connected to OPC UA Server!");\n\n                // Perform operations with the session...\n\n                // Close the session.\n                session.Close();\n                Console.WriteLine("Session closed.");\n            }\n        }\n    }