public static void RegisterRoutes(RouteCollection routes)\n{\n    routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n    routes.IgnoreRoute("elmah.axd");\n    routes.IgnoreRoute("Content/{*pathInfo}");  // Ignore static content like CSS, JS\n    routes.IgnoreRoute("Scripts/{*pathInfo}");  // Ignore static content like JS\n    routes.IgnoreRoute("{*favicon}", new { favicon = @"(.*/)?favicon.ico(/.*)?" });  // Ignore favicon\n    routes.MapRoute(\n        "Default",                                              // Route name\n        "{controller}/{action}/{id}",                           // URL with parameters\n        new { controller = "Home", action = "Index", id = "" }  // Parameter defaults\n    );\n}