using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<List<UInt32>> listOfLists = new List<List<UInt32>>()\n        {\n            new List<UInt32> { 32, 1, 1, 1, 1, 1 },\n            new List<UInt32> { 22, 2, 2, 2, 2, 2 },\n            new List<UInt32> { 32, 1, 1, 1, 1, 1 },\n            new List<UInt32> { 1, 1, 1, 1, 1 }\n        };\n\n        // Sorting in ascending order based on the first element of each inner list\n        var sortedAsc = listOfLists.OrderBy(innerList => innerList[0]).ToList();\n\n        Console.WriteLine("Sorted in ascending order:");\n        foreach (var list in sortedAsc)\n        {\n            Console.WriteLine(string.Join(",", list));\n        }\n\n        // Sorting in descending order based on the first element of each inner list\n        var sortedDesc = listOfLists.OrderByDescending(innerList => innerList[0]).ToList();\n\n        Console.WriteLine("\nSorted in descending order:");\n        foreach (var list in sortedDesc)\n        {\n            Console.WriteLine(string.Join(",", list));\n        }\n    }\n}