using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    public static async Task Main(string[] args)\n    {\n        // The URL of the target website\n        string url = "https://www.example.com/receiveHtml";\n\n        // The HTML content to send\n        string htmlContent = "<html><body><h1>Hello, World!</h1></body></html>";\n\n        // Create an instance of HttpClient\n        using (HttpClient client = new HttpClient())\n        {\n            // Create the content to be sent\n            StringContent content = new StringContent(htmlContent, Encoding.UTF8, "text/html");\n\n            try\n            {\n                // Send the POST request\n                HttpResponseMessage response = await client.PostAsync(url, content);\n\n                // Ensure the request was successful\n                response.EnsureSuccessStatusCode();\n\n                // Optionally, read the response content\n                string responseBody = await response.Content.ReadAsStringAsync();\n                Console.WriteLine("Response received: " + responseBody);\n            }\n            catch (HttpRequestException e)\n            {\n                // Handle any errors that occur during the request\n                Console.WriteLine("Request error: " + e.Message);\n            }\n        }\n    }\n}