public partial class MainForm : Form\n{\n    private IDatabaseHelper _databaseHelper;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        string provider = "sqlserver"; // or "mysql", decide at runtime\n        string connectionString = "your_connection_string";\n        _databaseHelper = DatabaseHelperFactory.CreateDatabaseHelper(provider, connectionString);\n\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        string query = "SELECT * FROM YourTable";\n        DataSet dataSet = _databaseHelper.GetDataSet(query);\n        BindingSource bindingSource = new BindingSource { DataSource = dataSet.Tables[0] };\n        dataGridView.DataSource = bindingSource;\n    }\n}