using System.IO;\nusing System.Web.UI;\n\npublic class CustomPage : Page\n{\n    protected override void Render(HtmlTextWriter writer)\n    {\n        // Create a StringWriter to capture the output\n        StringWriter stringWriter = new StringWriter();\n        HtmlTextWriter htmlWriter = new HtmlTextWriter(stringWriter);\n\n        // Call the base Render method to generate the HTML\n        base.Render(htmlWriter);\n\n        // Get the generated HTML\n        string htmlOutput = stringWriter.ToString();\n\n        // Perform your string manipulations here\n        string modifiedHtmlOutput = htmlOutput.Replace("oldString", "newString");\n\n        // Write the modified HTML to the output\n        writer.Write(modifiedHtmlOutput);\n    }\n}