using System;\nusing System.Net;\nusing System.Net.Security;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class HttpsServer\n{\n    public static void StartServer(string certPath, string password)\n    {\n        X509Certificate2 certificate = new X509Certificate2(certPath, password);\n        HttpListener listener = new HttpListener();\n        listener.Prefixes.Add("https://localhost:443/");\n        listener.Start();\n\n        listener.BeginGetContext(new AsyncCallback(ListenerCallback), listener);\n\n        Console.WriteLine("Listening...");\n        Console.ReadLine();\n        listener.Stop();\n    }\n\n    private static void ListenerCallback(IAsyncResult result)\n    {\n        HttpListener listener = (HttpListener)result.AsyncState;\n        HttpListenerContext context = listener.EndGetContext(result);\n        HttpListenerRequest request = context.Request;\n        HttpListenerResponse response = context.Response;\n\n        response.StatusCode = 200;\n        response.ContentType = "text/plain";\n        using (System.IO.StreamWriter writer = new System.IO.StreamWriter(response.OutputStream))\n        {\n            writer.WriteLine("Hello, world!");\n        }\n\n        response.Close();\n    }\n}