using System.Data.SqlClient;\n\npublic class Client\n{\n    private string connectionString = "your_connection_string";\n\n    public void ExecuteQuery(string query, params SqlParameter[] parameters)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddRange(parameters);\n                connection.Open();\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}\n\n// Example usage\npublic void ExampleUsage()\n{\n    Client client = new Client();\n    string insertQuery = "INSERT INTO customers (Id, Name, ContactPerson, Address, TinNo, ContactNo, Report, Sync, Ids) VALUES (@Id, @Name, @ContactPerson, @Address, @TinNo, @ContactNo, @Report, @Sync, @Ids)";\n    SqlParameter[] parameters = new SqlParameter[]\n    {\n        new SqlParameter("@Id", SqlDbType.VarChar) { Value = row["Id"].ToString() },\n        new SqlParameter("@Name", SqlDbType.VarChar) { Value = row["Name"].ToString() },\n        new SqlParameter("@ContactPerson", SqlDbType.VarChar) { Value = row["ContactPerson"].ToString() },\n        new SqlParameter("@Address", SqlDbType.VarChar) { Value = row["Address"].ToString() },\n        new SqlParameter("@TinNo", SqlDbType.VarChar) { Value = row["TinNo"].ToString() },\n        new SqlParameter("@ContactNo", SqlDbType.VarChar) { Value = row["Contactno"].ToString() },\n        new SqlParameter("@Report", SqlDbType.VarChar) { Value = row["Report"].ToString() },\n        new SqlParameter("@Sync", SqlDbType.Int) { Value = 0 },\n        new SqlParameter("@Ids", SqlDbType.VarChar) { Value = row["Id"].ToString() }\n    };\n    client.ExecuteQuery(insertQuery, parameters);\n}