using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private RichTextBox richTextBox;\n\n    public MyForm()\n    {\n        richTextBox = new RichTextBox();\n        richTextBox.Text = "This is a normal text. This is bold text.";\n        // Set the font for the bold text\n        richTextBox.Select(27, 11); // Select the text "bold text"\n        richTextBox.SelectionFont = new System.Drawing.Font(richTextBox.SelectionFont, System.Drawing.FontStyle.Bold);\n\n        richTextBox.Dock = DockStyle.Fill;\n        this.Controls.Add(richTextBox);\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MyForm());\n    }\n}