using System;\nusing System.Data;\n\npublic class DatasetComparer\n{\n    public void CompareAndSyncDatasets(DataSet ds, DataSet clientDS, Client client)\n    {\n        if (ds.Tables[0].Rows.Count > 0 || clientDS.Tables[0].Rows.Count > 0)\n        {\n            foreach (DataRow row in ds.Tables[0].Rows)\n            {\n                string id = row["Id"].ToString();\n                DataRow[] foundRows = clientDS.Tables[0].Select($"Id = '{id}'");\n\n                if (foundRows.Length > 0)\n                {\n                    // Update query\n                    string updateQuery = $"UPDATE customers SET Name = '{row["Name"]}', ContactPerson = '{row["ContactPerson"]}', Address = '{row["Address"]}', TinNo = '{row["TinNo"]}', ContactNo = '{row["Contactno"]}', Report = '{row["Report"]}', Sync = 0, Ids = '{row["Id"]}' WHERE Id = '{row["Id"]}'";\n                    client.GetSingleValue(updateQuery);\n                }\n                else\n                {\n                    // Insert query\n                    string insertQuery = $"INSERT INTO customers(Id, Name, ContactPerson, Address, TinNo, ContactNo, Report, Sync, Ids) VALUES('{row["Id"]}', '{row["Name"]}', '{row["ContactPerson"]}', '{row["Address"]}', '{row["TinNo"]}', '{row["Contactno"]}', '{row["Report"]}', 0, '{row["Id"]}')";\n                    client.GetSingleValue(insertQuery);\n                }\n            }\n        }\n    }\n}\n\n// Assuming you have a Client class with a GetSingleValue method to execute your queries.\npublic class Client\n{\n    public void GetSingleValue(string query)\n    {\n        // Execute your query here\n    }\n}