public class UserController : Controller\n{\n    public ActionResult Details(int id, string format)\n    {\n        var userModel = GetUserById(id); // Assume this method gets the user data\n\n        if (format != null && format.ToLower() == "xml")\n        {\n            return new XmlResult(userModel);\n        }\n\n        return View(userModel);\n    }\n\n    private UserModel GetUserById(int id)\n    {\n        // Retrieve your user model from the database or other source\n        return new UserModel\n        {\n            Id = id,\n            Name = "btyndall"\n            // Add other properties as needed\n        };\n    }\n}\n\npublic class UserModel\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    // Add other properties as needed\n}