using System;\nusing System.IO;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example log data\n        var logs = new[] {\n            new { Time = "2023-10-01T10:00:00", Message = "Test started" },\n            new { Time = "2023-10-01T10:05:00", Message = "Test completed" }\n        };\n\n        // Create XML document\n        var xml = new XElement("LogReport",\n            new XElement("Logs",\n                new XElement("Log",\n                    new XElement("Time", logs[0].Time),\n                    new XElement("Message", logs[0].Message)\n                ),\n                new XElement("Log",\n                    new XElement("Time", logs[1].Time),\n                    new XElement("Message", logs[1].Message)\n                )\n            )\n        );\n\n        // Save XML to file\n        xml.Save("logReport.xml");\n\n        // Create XHTML document\n        var xhtml = new XElement("html",\n            new XAttribute("xmlns", "http://www.w3.org/1999/xhtml"),\n            new XElement("head",\n                new XElement("title", "Log Report")\n            ),\n            new XElement("body",\n                new XElement("h1", "Log Report"),\n                new XElement("table",\n                    new XElement("thead",\n                        new XElement("tr",\n                            new XElement("th", "Time"),\n                            new XElement("th", "Message")\n                        )\n                    ),\n                    new XElement("tbody",\n                        new XElement("tr",\n                            new XElement("td", logs[0].Time),\n                            new XElement("td", logs[0].Message)\n                        ),\n                        new XElement("tr",\n                            new XElement("td", logs[1].Time),\n                            new XElement("td", logs[1].Message)\n                        )\n                    )\n                )\n            )\n        );\n\n        // Save XHTML to file\n        using (StreamWriter writer = new StreamWriter("logReport.xhtml"))\n        {\n            writer.Write(xhtml.ToString(SaveOptions.DisableFormatting));\n        }\n    }\n}