// Assume key and plaintext are both 16 bytes (128 bits)\nbyte[] plaintextBlock = ... // First 16 bytes of plaintext ("GIF8...")\nbyte[] key = ... // 128-bit key\n\n// Key Expansion\nbyte[][] roundKeys = KeyExpansion(key);\n\n// Initial Round\nbyte[] state = XOR(plaintextBlock, roundKeys[0]);\n\n// Main Rounds (9 rounds for AES-128)\nfor (int round = 1; round <= 9; round++) {\n    state = SubBytes(state);\n    state = ShiftRows(state);\n    state = MixColumns(state);\n    state = XOR(state, roundKeys[round]);\n}\n\n// Final Round (10th round)\nstate = SubBytes(state);\nstate = ShiftRows(state);\nstate = XOR(state, roundKeys[10]);\n\n// `state` now contains the encrypted first block