try {\n       // Try to save in HKLM (requires admin rights)\n       RegistryKey key = Registry.LocalMachine.CreateSubKey(@"SOFTWARE\MyApp");\n       key.SetValue("ActivationKey", activationKey);\n       key.Close();\n   } catch (UnauthorizedAccessException) {\n       // Fallback to HKCU (user-specific)\n       RegistryKey key = Registry.CurrentUser.CreateSubKey(@"SOFTWARE\MyApp");\n       key.SetValue("ActivationKey", activationKey);\n       key.Close();\n   }