public void SaveData(MyDataObject data)\n   {\n       // Save to new system database\n       SaveToDatabase(data);\n\n       // Call legacy system web service\n       using (var client = new HttpClient())\n       {\n           client.BaseAddress = new Uri("http://legacy-system-url");\n           var response = client.PostAsJsonAsync("api/save", data).Result;\n           response.EnsureSuccessStatusCode();\n       }\n   }