private void PollDatabases()\n   {\n       while (true)\n       {\n           // Poll new system database\n           var newSystemChanges = GetChangesFromDatabase("newSystemConnectionString");\n           foreach (var change in newSystemChanges)\n           {\n               ForwardChangeToLegacySystem(change);\n           }\n\n           // Poll legacy system database\n           var legacySystemChanges = GetChangesFromDatabase("legacySystemConnectionString");\n           foreach (var change in legacySystemChanges)\n           {\n               ForwardChangeToNewSystem(change);\n           }\n\n           // Sleep for 2 seconds\n           Thread.Sleep(2000);\n       }\n   }\n\n   private IEnumerable<MyDataObject> GetChangesFromDatabase(string connectionString)\n   {\n       // Implementation to get changes since last poll\n   }\n\n   private void ForwardChangeToLegacySystem(MyDataObject change)\n   {\n       // Implementation to forward change to legacy system\n   }\n\n   private void ForwardChangeToNewSystem(MyDataObject change)\n   {\n       // Implementation to forward change to new system\n   }