int minY = Int32.MaxValue;\nint maxY = Int32.MinValue;\n\nvar results = _average\n    .Skip(firstSample + 1)\n    .Take(lastSample - firstSample)\n    .AsParallel()\n    .Aggregate(\n        () => (min: Int32.MaxValue, max: Int32.MinValue),\n        (acc, Y) => (Math.Min(acc.min, Y), Math.Max(acc.max, Y)),\n        (acc1, acc2) => (Math.Min(acc1.min, acc2.min), Math.Max(acc1.max, acc2.max)),\n        acc => acc\n    );\n\nminY = results.min;\nmaxY = results.max;