@(Html.ShieldGrid()\n       .Name("grid")\n       .DataSource(ds => ds\n           .Remote()\n           .Read(read => read.Action("Action", "Controller"))\n       )\n       .Columns(columns =>\n       {\n           columns.Add().Field("PropertyName").Title("Header").Width(100);\n           // Add more columns as needed\n       })\n       .Paging(true)\n       .Sorting(true)\n   )