using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    SqlTransaction transaction = connection.BeginTransaction();\n    try\n    {\n        foreach (var item in items)\n        {\n            SqlCommand command = new SqlCommand($"INSERT INTO YourTable (Column1, Column2) VALUES (@Value1, @Value2)", connection, transaction);\n            command.Parameters.AddWithValue("@Value1", item.Value1);\n            command.Parameters.AddWithValue("@Value2", item.Value2);\n            command.ExecuteNonQuery();\n        }\n        transaction.Commit();\n    }\n    catch (Exception)\n    {\n        transaction.Rollback();\n        throw;\n    }\n}