String ldapServer = "ldap://your-ldap-server.com"; // Replace with your LDAP server address\nString domain = "yourdomain.com"; // Replace with your domain\nPrincipalContext context = new PrincipalContext(ContextType.Domain, ldapServer, domain);\n\nif (context.ConnectedServer != null) {\n    System.out.println("Successfully connected to: " + context.ConnectedServer);\n} else {\n    System.out.println("Failed to connect to the LDAP server.");\n}