public class DALProduct {\n    public static Product GetByID(int id, string currentUICulture) {\n        using (var session = NHibernateHelper.OpenSession()) {\n            var product = session.Get<Product>(id);\n\n            var translation = product.Translations\n                .FirstOrDefault(t => t.Language == currentUICulture)\n                ?? product.Translations.FirstOrDefault(t => t.Language == "en"); // fallback to default language\n\n            if (translation != null) {\n                product.Name = translation.Name;\n                product.Description = translation.Description;\n            }\n\n            return product;\n        }\n    }\n}